INSERT INTO `audiobooks_narrators` (`audiobook_id`, `narrator_id`, `bio`, `image`) VALUES
(2, 41, '<p><span style="font-family: " lang="EN-US">&nbsp;&nbsp;&nbsp;&nbsp; Also known as Robert Whitfield.&nbsp;</span><span style="font-family: " lang="EN-US">He is the winner of numerous<em> <span style="font-family: ">Earphones Awards</span></em> and the prestigious <em><span style="font-family: ">Audie&reg; Award</span> </em>in 2006. <br /></span><span style="font-family: " lang="EN-US">&nbsp;&nbsp;&nbsp;&nbsp; Born in England, he worked for ten years as a radio news announcer for the <em><span style="font-family: ">BBC</span></em> and as a Narrator for the <em><span style="font-family: ">Royal National Institute for the Blind</span></em>. Besides narrating, he is involved in numerous stage-acting projects in the United States and Europe.</span></p>', '.png'),
(6, 28, '<p>&nbsp;&nbsp;&nbsp;&nbsp; Actor, author, songwriter, and playwright.<br />&nbsp;&nbsp;&nbsp;&nbsp; He and his wife, Jahnna Beecham, have written more than 100 books for teens and young readers under the name Jahnna N. Malcolm, including the best-selling series <em>The Jewel Kingdom. Their musicals, Chaps!</em> and <em>They Came from Way out There</em> (with Michael J. Hume), have played in theaters across the United States.<br />&nbsp;&nbsp;&nbsp;&nbsp; Hillgartner&rsquo;s audiobook credits include works by<em> Dean Koontz, Nelson Algren</em>, and<em> William F. Buckley Jr</em>.</p>', '.png'),
(7, 52, '<p>&nbsp;&nbsp;&nbsp;&nbsp; A recording artist and spoken-word performer whose voice acting has been featured in several independent films.<br />&nbsp;&nbsp;&nbsp;&nbsp; With a background in <em>Vaudeville</em>, she spent many years singing, writing, and producing multimedia performances for her pop band.<br />&nbsp;&nbsp;&nbsp;&nbsp; Her<strong> </strong><em>one-woman, spoken-word show</em> has been staged across the western United States.</p>', '.png'),
(8, 47, '<p>&nbsp;&nbsp;&nbsp;&nbsp; A dialogue director and voice artist best known for his voice roles in CD-ROM games and<strong> </strong>TV shows such as <em>Buffy the Vampire Slayer </em>and <em>Transformers</em>.<br />&nbsp;&nbsp;&nbsp;&nbsp; He is a former member of the <em>Oregon Shakespeare Festival.</em><br />&nbsp;&nbsp;&nbsp;&nbsp; An <em>Earphones Award</em> winner and <em>Audie&reg; Award </em>nominee. He has narrated more than 100 books for<em> Blackstone Audio</em>.</p>', '.png'),
(13, 29, '<p>&nbsp;&nbsp;&nbsp;&nbsp; She has had many incarnations, including private detective, classical musician, actress, television talk-show host, but shefound her true calling reading books for the blind and physically handicapped for the <em>Library of Congress</em> program, <em>Talking Books. <br /></em>&nbsp;&nbsp;&nbsp;&nbsp; The fact that she can work with <em>Blackstone Audio</em> from the beauty of the mountains of southern Oregon is an unexpected bonus.</p>', '.png'),
(15, 43, '<p>&nbsp;&nbsp;&nbsp;&nbsp; A <em>Grammy</em>-winning audiobook producer. Has narrated more than 100 audiobooks. A recipient of multiple <em>AudioFile Earphones Awards.<br /><strong>&nbsp;&nbsp;&nbsp;&nbsp;</strong></em> He was awarded the coveted <em>Audie&reg; Award</em> for solo narration in both 2005 and 2007, and was named as one of <em>AudioFile&rsquo;s Best Voices of the Year</em> in 2008.</p>', '.png'),
(17, 51, '<p>&nbsp;&nbsp;&nbsp;&nbsp; A published Narrator of children&rsquo;s books. <br />&nbsp;&nbsp;&nbsp;&nbsp; Some of his the published credits with Library Edition include <em>The Voyages of Doctor Dolittle, For the Temple </em>and <em>The Young Carthaginian</em>.</p>', ''),
(18, 47, '<p>An <em>Earphones Award</em> winner and <em>Audie&reg; Award</em> nominee. He has narrated more than 100 books for <em>Blackstone</em> <em>Audio.<br /></em>&nbsp;&nbsp;&nbsp;&nbsp; A dialogue director and voice artist best known for his voice roles in CD-ROM games and TV shows such as<em> Buffy the Vampire Slayer </em>and <em>Transformers.<br />&nbsp;&nbsp;&nbsp;&nbsp; </em>He is a former member of the <em>Oregon Shakespeare Festival</em>.</p>', '.png'),
(19, 49, '<p>&nbsp;&nbsp;&nbsp;&nbsp; Attended the <em>University of Southern Maine</em> in Portland-Gorham before pursuing a career in voice work in<em> London</em>. <br />&nbsp;&nbsp;&nbsp;&nbsp; He has garnered 13 <em>Earphones Awards</em> and was voted one of "<em>The Best Voices at the End of the Century</em>" by AudioFile magazine.</p>', '.png'),
(20, 3, '<p>&nbsp;&nbsp;&nbsp;&nbsp; Has appeared in films, television, and regional theaters throughout the West Coast. <br />&nbsp;&nbsp;&nbsp;&nbsp; For the last decade he has been a member of the resident acting company at the<em> Oregon Shakespeare Festival</em>. <br />&nbsp;&nbsp;&nbsp;&nbsp; A native Californian, he divides his time between Los Angeles and Ashland, Oregon.</p>', '.png'),
(21, 6, '<p>&nbsp;&nbsp;&nbsp;&nbsp; After graduating from university with a Law degree he worked at the <em>High Court of Justice</em> in London, and in his spare time worked as a reporter on Capital Radio&rsquo;s <em>The Way It Is</em> news magazine show. <br />&nbsp;&nbsp;&nbsp;&nbsp; With the show&rsquo;s success he became a full-time journalist, winning a number of awards for his travel reporting. <br />&nbsp;&nbsp;&nbsp;&nbsp; This became a series of travel specials called Wildfire on <em>BBC Radio</em>. <br />&nbsp;&nbsp;&nbsp;&nbsp; In 1987 he won the Best Light Entertainment Writer Award at the New York Radio Festival for his series <em>Around the World in Ninety Seconds</em>.</p>', '.png'),
(22, 1, '<p>&nbsp;&nbsp;&nbsp;&nbsp; Was one of the most respected Narrators in the industry(1965-2006).<br />&nbsp;&nbsp;&nbsp;&nbsp; Wwinner of more than a dozen <em>Earphones Awards</em> and the prestigious<em> Audie&reg; Award</em> in 2004. <br />&nbsp;&nbsp;&nbsp;&nbsp; Trained at the <em>Actors Theatre of Louisvill</em>e, she was also a Director, Producer, and technician of her own studio, <em>Cedar House Audio</em>.</p>', '.png'),
(25, 43, '<p>&nbsp;&nbsp;&nbsp;&nbsp; A <em>Grammy</em>-winning audiobook producer. <br />&nbsp;&nbsp;&nbsp;&nbsp; Has narrated more than <em>100 audiobooks. <br /></em>&nbsp;&nbsp;&nbsp;&nbsp; A recipient of multiple <em>AudioFile Earphones Awards</em>, he was awarded the coveted <em>Audie&reg; Award</em> for solo narration in both 2005 and 2007, and was named as one of <em>AudioFile&rsquo;s Best Voices of the Year</em> in 2008.</p>', '.png'),
(26, 20, '<p>&nbsp;&nbsp;&nbsp;&nbsp; An American actor,comedian and singer, best known for his role as <em>George Costanza</em> on the television series <em>Seinfeld</em>.</p>', '.png'),
(27, 43, '<p>&nbsp;&nbsp;&nbsp;&nbsp; A <em>Grammy</em>-winning audiobook producer. <br />&nbsp;&nbsp;&nbsp;&nbsp; Has narrated more than <em>100 audiobooks</em>. A recipient of multiple <em>AudioFile Earphones Awards</em>, he was awarded the coveted <em>Audie&reg; Award</em> for solo narration in both 2005 and 2007, and was named as one of <em>AudioFile&rsquo;s Best Voices of the Year</em> in 2008.</p>', '.png'),
(29, 48, '', ''),
(30, 27, '<p>&nbsp;&nbsp;&nbsp;&nbsp; Named as one of <em>AudioFile</em> magazine&rsquo;s <em>Best Voices of the Year</em>, has received numerous <em>Audie Award</em> nominations and <em>AudioFile Earphones</em> awards for her narrations. <br />&nbsp;&nbsp;&nbsp;&nbsp; An experienced stage actress, she has also guest-starred on many top television series and stars in Director Sam Raimi&rsquo;s film <em>Drag Me to Hell</em>.</p>', '.png'),
(31, 55, '<p>&nbsp;&nbsp;&nbsp;&nbsp; One of&nbsp;the leading <em>Writer/Producer/Director</em> of audio drama in the United States today. <br />&nbsp;&nbsp;&nbsp;&nbsp; He is also a distinguished Narrator, who garnered 2<em> Audie&reg; Award The Incredible Shrinking Man</em> nominations in 2007 for his readings of and, with life-partner Lorna Raver, <em>Paris Stories. </em><br />&nbsp;&nbsp;&nbsp;&nbsp; His numerous honors include two <em>Peabody Awards</em>, eight <em>Audies</em>, and a <em>Grammy&reg;</em>. <br />&nbsp;&nbsp;&nbsp;&nbsp; He and Ms Raver live in Los Angeles.</p>', '.png'),
(31, 27, '<p>&nbsp;&nbsp;&nbsp;&nbsp; Named as one of <em>AudioFile</em> magazine&rsquo;s <em>Best Voices of the Year</em>, has received numerous<em> Audie Award</em> nominations and <em>AudioFile Earphones</em> awards for her narrations. <br />&nbsp;&nbsp;&nbsp;&nbsp; An experienced stage actress, she has also guest-starred on many top television series and stars in Director Sam Raimi&rsquo;s film <em>Drag Me to Hell</em>.</p>', '.png'),
(32, 13, '', ''),
(33, 4, '<p>&nbsp;&nbsp;&nbsp;&nbsp; Winner of 6 <em>AudioFile Earphones Awards</em> and has twice been nominated for the prestigious<em> Audie&reg; Award</em>. <br />&nbsp;&nbsp;&nbsp;&nbsp; She studied at the <em>Royal National Theatre in London</em> and the <em>Studio Theater</em> in Washington, D.C., and has appeared at the Kennedy Center and off Broadway. <br />&nbsp;&nbsp;&nbsp;&nbsp; She lives in Brooklyn, New York.</p>', '.png'),
(34, 22, '<p>&nbsp;&nbsp;&nbsp;&nbsp; 2009 <em>Best Voice in Fiction and Classics: The Count of Monte Cristo&nbsp;</em> (also here at <strong>www.MundoHablado.com</strong>)<br />&nbsp;&nbsp;&nbsp;&nbsp; 2008<em> Best Voice in Fiction &amp; Classics: The Solitude of Thomas Cave, The Whitet Tiger</em><br />&nbsp;&nbsp;&nbsp;&nbsp; He has read more than a <em>100 audiobooks</em> of every conceivable genre, including the numerous <em>Bestsellers</em> authored by <em>Ken Follet</em>.<br />&nbsp;&nbsp;&nbsp;&nbsp; In Southern California he has acted in productions at the <em>Mark Taper Forum</em>, <em>A Noise Within</em> and <em>The Globe Theaters</em> in San Diego.&nbsp;<br />&nbsp;&nbsp;&nbsp;&nbsp; He has also been seen on the stages of all of Seattle&rsquo;s major theaters as well as the <em>Tacoma Actors Guild </em>and the<em> New Mexico Rep</em>.</p>', '.png'),
(38, 51, '', ''),
(39, 48, '', ''),
(40, 2, '<p>&nbsp;&nbsp;&nbsp;&nbsp; Has earned <em>Tony</em> nominations and an <em>Obie Award</em> for his theater work. <br />&nbsp;&nbsp;&nbsp;&nbsp; He has had guest appearances in television&rsquo;s <em>Law &amp; Order, The X-Files, Miami Vice,</em> and <em>Boston Public </em>and starred as Dr. Frederick Chilton in the 1991 <em>Oscar&reg;</em>-winning film <em>Silence of the Lambs. <br /></em>&nbsp;&nbsp;&nbsp;&nbsp; He lives in Ashland, Oregon, with his family.</p>', '.png'),
(41, 21, '<p>&nbsp;&nbsp;&nbsp;&nbsp; Has appeared both on and <em>off Broadway</em> as well as in hundreds of radio and television commercials. <br />&nbsp;&nbsp;&nbsp;&nbsp; He has narrated books by such authors as <em>Larry McMurtry, Jay McInerney </em>and <em>Andy McNab.</em></p>', '.png'),
(42, 43, '<p>&nbsp;&nbsp;&nbsp;&nbsp; A Grammy-winning audiobook Producer. <br />&nbsp;&nbsp;&nbsp;&nbsp; Has narrated more than 100 audiobooks. <br />&nbsp;&nbsp;&nbsp;&nbsp; A recipient of multiple <em>AudioFile Earphones Awards</em>, he was awarded the coveted <em>Audie&reg; Award </em>for solo narration in both 2005 and 2007, and was named as one of <em>AudioFile&rsquo;s Best Voices of the Year </em>in 2008.</p>', '.png'),
(43, 16, '<p>&nbsp;&nbsp;&nbsp;&nbsp; Also known as&nbsp;Geoffrey Howard, a British former journalist who changed careers to become a Narrator and screen and stage actor, has performed in more than one hundred professional theater roles. <br />&nbsp;&nbsp;&nbsp;&nbsp; Several of his narrations have been named "<em>Audio Best of the Year</em>" by <em>Publishers Weekly</em>, and he has been a three-time nominee for the <em>Audie&reg; Award.</em></p>', '.png'),
(52, 12, '<p>&nbsp;&nbsp;&nbsp;&nbsp; Was born in London (1932-2005) and trained at the <em>Royal Academy of Dramatic Art</em>.&nbsp; <br />&nbsp;&nbsp;&nbsp;&nbsp; He performed in <em>BBC</em> radio plays before coming to America in 1976.<br />&nbsp;&nbsp;&nbsp;&nbsp; The Narrator of more than 800 audiobooks, he garnered numerous <em>Earphones Awards</em> and a <em>Grammy</em> nomination for his readings. <br />&nbsp;&nbsp;&nbsp; He was named a <em>Golden Voice</em> by <em>AudioFile</em> magazine in 1997.</p>', '.png'),
(54, 43, '<p>&nbsp;&nbsp;&nbsp;&nbsp; A <em>Grammy</em>-winning audiobook Producer. <br />&nbsp;&nbsp;&nbsp;&nbsp; Has narrated more than 100 audiobooks. <br />&nbsp;&nbsp;&nbsp;&nbsp; A recipient of multiple <em>AudioFile Earphones Awards</em>, he was awarded the coveted<em> Audie&reg; Award</em> for solo narration in both 2005 and 2007, and was named as one of <em>AudioFile&rsquo;s Best Voices of the Year</em> in 2008.</p>', '.png'),
(56, 41, '<p>&nbsp;&nbsp;&nbsp;&nbsp; Winner of numerous <em>Earphones Awards </em>and the prestigious<em> Audie&reg; Award</em> in 2006. <br />&nbsp;&nbsp;&nbsp;&nbsp; Born in England, he worked for ten years as a radio news announcer for the <em>BBC</em> and as a narrator for the <em>Royal National Institute for the Blind.</em> <br />&nbsp;&nbsp;&nbsp;&nbsp; Besides narrating, he is involved in numerous stage-acting projects in the United States and Europe.</p>', '.png'),
